const scrollContainer=document.querySelector('.main');let currentPost;document.addEventListener('postLoaded',function(a){currentPost=a.detail.name,console.log(`setting scrollTop:${currentPost} to ${sessionStorage.getItem(`scrollTop:${currentPost}`)}`),scrollContainer.scrollTop=sessionStorage.getItem(`scrollTop:${currentPost}`)}),scrollContainer.addEventListener('scroll',function(){return sessionStorage.setItem(`scrollTop:${currentPost}`,scrollContainer.scrollTop)});